    public static final String LOCK_PATH = "/curator-kick-off/lock";
    private static CuratorFramework client;

    @BeforeClass
    public static void before() {
        client = CuratorFrameworkFactory.newClient(
                "127.0.0.1:2181",
                1000 * 10,
                3000, new ExponentialBackoffRetry(
                        1000,
                        3));
        client.start();
    }

