        InterProcessMutex lock = new InterProcessMutex(client, LOCK_PATH);
        boolean acquire = lock.acquire(5, TimeUnit.SECONDS);
        System.out.println(acquire);
        System.out.println("done");
        lock.release();
        System.in.read();
    }
}
